/*==================*/
/*  HIDE SOME SHIT  */
/*==================*/
.b-post__lastepisodeout,
.b-post__social_holder_wrapper,
.b-post__rating_table,
.b-post__qa_list_block,
.b-post__mtitle,
.b-post__mixedtext,
.b-post__support_holder,
.b-content__htitle,
.b-content__inline_sideprobar,
.b-content__crumbs,
.b-content__sidebar,
.b-collections__newest,
.b-footer,
.b-inlineads__icon.mini,
.b-translators__title,
.b-seriesupdate__block_date .act,
.b-sideactions__reviews,
.b-wrapper.nopadd,
.comments-form,
.b-post__status_logo,
.b-post__go_status,
a#ownplayer-link-show,
#hd-comments-list,
#vk_groups,
#hd-comments-list,
#hd-comments-navigation,
.b-sidetitle,
.b-sidelist__holder,
.b-post__schedule,
*[id^="heipf"] {
    display: none !important;
}

.b-content__main div:has(a[target="_blank"] > img[src*="https://static.hdrezka.ac/i/def_"])
{
    display: none !important;
}

body.active-brand.pp {
    padding-top: 0 !important;
}

/*===========*/
/*  NAVBAR   */
/*===========*/
.b-topnav {
    max-width: 960px;
}
.b-topnav_wrapper {
    background-image: none;
    background-color: #efefef;
    box-shadow: none;
    border: none;
    margin-bottom: 20px;
}
.b-search__form {
    width: 15rem;
    background-color: transparent;
}
.b-search__field {
    background-color: transparent;
}
.b-search__field:focus {
    background-color: #fff;
}

/*======================*/
/*  TYPOGRAPHY          */
/* modernfontstacks.com */
/*======================*/
body {
    font-family: system-ui, sans-serif;
    font-weight: normal;
}

h1,
h2 {
    /* modernfontstacks.com */
    font-family: Avenir, Montserrat, Corbel, "URW Gothic", source-sans-pro, sans-serif;
}

/*===============*/
/*  MAIN LAYOUT  */
/*===============*/
body,
#wrapper,
body.active-brand #wrapper {
    width: auto;
    background-color: #ddd !important;
}

.b-container {
    padding: 20px 60px 80px;
    max-width: 960px;
}

.b-content__columns {
    padding: initial;
}

/*===============*/
/*  PLAYER       */
/*===============*/
.b-translators__block {
    margin-top: 2rem;
}

#player {
    --margin: calc(50% - 48vw);
    margin-left: var(--margin);
    margin-right: var(--margin);
}
:root .b-premium_user__body .b-player__holder_cdn,
:root .b-player__holder_cdn {
    position: relative;
    height: 0 !important;
    width: 100% !important;
    padding-bottom: min(56%, 80vh);
}
:root .b-premium_user__body .b-player__container_cdn,
:root .b-player__container_cdn {
    position: absolute!important;
    height: 100% !important;
    width: 100% !important;
    top: 0!important;
    bottom: 0!important;
    left: 0!important;
    right: 0!important;
}

body {
    font-family: system-ui, sans-serif;
}
h1,
h2,
h3 {
    font-family: Avenir, Montserrat, Corbel, 'URW Gothic', source-sans-pro, sans-serif;
}

/* Sidebar */
#aside,
.app-aside,
.app-aside .scroll {
    width: 28rem;
}
#aside {
    top: 0;
    left: 0;
    bottom: auto;
    height: 3.5rem;
}
#aside .navside {
    position: static;
    background-color: transparent;
}
#aside .navside .navbar {
    display: none;
}
#aside .navside .nav {
    display: flex;
    flex-direction: row;
    align-items: center;
    gap: 0.5rem;
    padding-right: 1rem;
    padding-left: 1rem;
    height: 3.5rem;
}
#aside .navside .nav li {
    display: none;
}
#aside .navside .nav li:nth-child(12),
#aside .navside .nav li:nth-child(13),
#aside .navside .nav li:nth-child(19) {
    display: block;
}

#aside .navside .nav li a {
    display: inline-flex;
    white-space: nowrap;
    gap: 0.5rem;
}
#aside .navside .nav li a > * {
    all: unset;
}

/* Header */
.app-header {
    padding-left: 30rem;
}

/* Content */
#content {
    margin-left: 0;
    margin-top: 0;
}

.padding {
    padding: 2rem 5rem;
}

/* Bookmarks */
div[id^="favorites-item-"],
.card-block {
    position: relative;
}
div[id^="favorites-item-"] a,
.card-block a {
    z-index: 2;
    position: relative;
}
div[id^="favorites-item-"] .item-title a,
.card-block h6 a {
    z-index: 0;
    position: initial;
}
div[id^="favorites-item-"] .item-title a::before,
.card-block h6 a::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: 1;
}

.episode-number a {
    display: inline-block;
    padding: 0.5rem 0.7rem;
    z-index: 100;
    text-decoration: underline;
}
.episode-number a:hover {
    text-decoration: none;
}

h3 select {
    max-width: 8rem;
    background-color: transparent;
    float: right;
    padding: 0.3rem 0.5rem;
}
h3 select + select {
    margin-right: 1rem;
}

body {
     --font-size-medium:1rem;
     --line-height-medium:1.75;
    font-family: system-ui, sans-serif; 
}

.mw-body h1, .mw-body .mw-heading1, .mw-body-content h1, .mw-body-content .mw-heading1, .mw-body-content h2, .mw-body-content .mw-heading2 {
    font-family: 'Iowan Old Style', 'Palatino Linotype', 'URW Palladio L', P052, serif;
}
.mw-body h1, .mw-body .mw-heading1, .mw-body-content h1, .mw-body-content .mw-heading1 {
    font-size: 2rem;
}

.vector-user-links {
    display: none;
}

