/* ==UserStyle==
@name           Впизду редизайн
@namespace      github.com/openstyles/stylus
@version        1.21
@description    Центрирование ленты с выбором её ширины, скрытие реакций/эмодзи и рекламы Plus и др.
@author         hydrozz
@preprocessor	stylus

@var checkbox --hideLive "Впизду панель справа" 1
@var checkbox --noEmoji "Впизду реакции / эмодзи" 1
@var checkbox --hidePlus "Впизду рекламу Plus" 1
@var checkbox --hideInfoPanel "Впизду ссылки 'О проекте', 'Правила' и т.д." 1
==/UserStyle== */
@-moz-document domain("dtf.ru")
{
    .andropov-media[style*="max-width: 592px"]
    {
        max-width: none !important;
    }

    .sidebar
    {
        scrollbar-width: thin;
        scrollbar-color: var(--scrollbar-color) transparent !important;
    }


    if --hideLive
    {
        .aside.aside--right
        {
            display: none;
        }
    }

    if --hideInfoPanel
    {
        .sidebar__section:last-child
        {
            display: none;
        }
    }

    if --hidePlus
    {
        .popover:has(.reaction-plus-restriction),
        .distribution,
        .comments-limit__expand-always,
        .supbar--top, 
        .account-menu__group:last-child,
        div[style$=" 32px;"],
        div[style$=" 64px;"],
        .rotator
        {
            display: none !important;
        }
    }

    if --noEmoji
    {
        /* Hide all reactions except heart */
        .reactions .reaction-button:not(:first-child),
        .reactions .reaction-button:has(.icon--reaction_add){
            display: none;
        }

        .reaction-button {
            background: transparent;
        }

        .content__floating
        {
            display: none;
        }

        .content__reactions
        {
            align-self: center;
            padding: 0 0 0 var(--layout-content-offset-x);
        }

        .content,
        .comment__content
        {
            display: grid;
            grid-template-columns: auto 1fr;
        }

        .comment__text
        {
            margin: 0;
        }

        .content > div,
        .comment__content > div
        {
            grid-column: span 2;
        }

        .content__reactions,
        .content-footer,
        .comment__reactions,
        .comment__actions
        {
            grid-column: span 1 !important;
        }

        .content__reactions,
        .content-footer
        {
            margin-top: 6px;
        }

        .comment__actions
        {
            margin-left: 30px;
        }

        .content-footer {
            padding-left: calc(var(--offset-x) / 2)
        }

        .content-footer__item > div,
        .content-views__icon
        {
            background-color: transparent !important;
        }
    }
}
